def plot_sequence(axs,in_sig,targ_sig,out_sig=None,**kwargs):
    n_targ = targ_sig.shape[1]
    for j,ax in  enumerate(axs[:-1]):
        ax.plot(targ_sig[:,j])
        if out_sig is not None:
            ax.plot(out_sig[:,j])
            ax.legend(['y','Å·'])
            if 'ref' in kwargs:
                ax.plot(kwargs['ref'][:,j])
        ax.label_outer()
    axs[-1].plot(in_sig)

